/**
 * Set the new value.
 *
 * @param rowreq_ctx
 *        Pointer to the users context. You should know how to
 *        manipulate the value from this object.
 * @param HansiState_val
 *        A long containing the new value.
 */
int
HansiState_set( dot11HansiTable_rowreq_ctx *rowreq_ctx, u_long HansiState_val )
{
    int type=0, ret=0;
    char status[10]={0};
    void *connection = NULL;
    char hansi[5]={0};
    DEBUGMSGTL(("verbose:dot11HansiTable:HansiState_set","called\n"));

    /** should never get a NULL pointer */
    netsnmp_assert(NULL != rowreq_ctx);
    
    if(rowreq_ctx->data.hansi_type == 1)
    {
	    return MFD_ERROR;
    }
    if(rowreq_ctx->data.HansiState == HansiState_val)
    {
	    rowreq_ctx->data.HansiState = HansiState_val;
	    return MFD_SUCCESS;
    }
    if(HansiState_val==HANSISTATE_BACK)
    {
	    strcpy(status,"disable");
    }
    else if(HansiState_val==HANSISTATE_MASTER)
    {
	    strcpy(status,"enable");
    }
    else
    {
	return MFD_ERROR;
    }

    if(SNMPD_DBUS_ERROR == get_slot_dbus_connection(rowreq_ctx->data.slot, &connection, SNMPD_INSTANCE_MASTER_V3))
    {
	    return MFD_ERROR;
    }
    
    sprintf(hansi,"%d",rowreq_ctx->data.instance);
    snmp_log(LOG_DEBUG,"HansiState_set  -----hansi=%s",hansi);
    ret = config_vrrp_service_web(hansi,status,connection);
    snmp_log(LOG_DEBUG,"HansiState_set  -----RET=%d",ret);
    if(ret==0)
    {
	    rowreq_ctx->data.HansiState = HansiState_val;
	    return MFD_SUCCESS;
    }
    else
    {
	    return MFD_ERROR;
    }

} /* HansiState_set */

